import Phaser from "phaser";

export default class GameScene extends Phaser.Scene {
  private player!: Phaser.GameObjects.Sprite;
  private mapImg!: Phaser.GameObjects.Image;
  private boxes: Phaser.GameObjects.Group | any;
  
  // GPS to Pixels conversion ratio (စမ်းသပ်ကြည့်ပြီး ညှိရန်)
  private SCALE = 200000; 

  constructor() {
    super("GameScene");
  }

  preload() {
    this.load.image("mapBackground", "/images/3.png");
    this.load.image("player", "/images/human.png");
    this.load.image("box", "/images/box.png");
  }

  create(data: { centerLat: number; centerLng: number; boxes: any[] }) {
    // ၁။ Map Background (အလယ်မှာ ထားမယ်)
    this.mapImg = this.add.image(0, 0, "mapBackground");

    // ၂။ Player (Screen ရဲ့ အလယ် [0,0] မှာ အမြဲရှိနေမယ်)
    this.player = this.add.sprite(0, 0, "player").setScale(0.5).setDepth(10);
    
    // ၃။ Camera ကို Player နောက်လိုက်ခိုင်းမယ်
    this.cameras.main.startFollow(this.player, true, 0.1, 0.1);
    this.cameras.main.setZoom(1.5);

    // ၄။ Boxes များကို Initial GPS ကနေ တွက်ချက်ပြီး ချထားမယ်
    this.boxes = this.add.group();
    data.boxes.forEach((box) => {
      const x = (box.lng - data.centerLng) * this.SCALE;
      const y = (data.centerLat - box.lat) * this.SCALE;
      const chest = this.add.sprite(x, y, "box").setScale(0.3).setInteractive();
      
      chest.on('pointerdown', () => {
         // ပစ္စည်းကောက်တဲ့ Logic ဒီမှာ ထည့်နိုင်ပါတယ်
         console.log("Collected:", box.type);
         chest.destroy();
      });
    });
  }

  // အပြင်ကနေ GPS update လာရင် ဒီ function ကို လှမ်းခေါ်ပေးရပါမယ်
  updatePlayerPosition(lat: number, lng: number, centerLat: number, centerLng: number) {
    if (!this.player) return;

    const newX = (lng - centerLng) * this.SCALE;
    const newY = (centerLat - lat) * this.SCALE;

    // ချောချောမွေ့မွေ့ ရွေ့သွားအောင် Tween သုံးမယ်
    this.tweens.add({
      targets: this.player,
      x: newX,
      y: newY,
      duration: 500,
      ease: 'Linear'
    });
  }
}
